pragma solidity ^0.8.13;

/**
 * @title MockENSRegistry
 * @notice Simple ENS-like registry mapping names to addresses
 */
contract MockENSRegistry {

    // ==================== STATE ====================

    address public owner;

    mapping(string => address) public names; // ensName => contract address
    string[] public registeredNames;

    // ==================== EVENTS ====================

    event NameRegistered(string ensName, address contractAddress);
    event NameUpdated(string ensName, address newAddress);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // ==================== MODIFIERS ====================

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor() {
        owner = msg.sender;
    }

    // ==================== FUNCTIONS ====================

    /**
     * @notice Register a name
     */
    function registerName(string memory _ensName, address _address) 
        external 
        onlyOwner 
    {
        require(_address != address(0), "Invalid address");
        require(names[_ensName] == address(0), "Name already registered");

        names[_ensName] = _address;
        registeredNames.push(_ensName);

        emit NameRegistered(_ensName, _address);
    }

    /**
     * @notice Update an existing name
     */
    function updateName(string memory _ensName, address _newAddress) 
        external 
        onlyOwner 
    {
        require(_newAddress != address(0), "Invalid address");
        require(names[_ensName] != address(0), "Name not registered");

        names[_ensName] = _newAddress;

        emit NameUpdated(_ensName, _newAddress);
    }

    /**
     * @notice Resolve a name to an address
     */
    function resolve(string memory _ensName) 
        external 
        view 
        returns (address) 
    {
        address resolved = names[_ensName];
        require(resolved != address(0), "Name not found");
        return resolved;
    }

    /**
     * @notice Check if a name is registered
     */
    function isRegistered(string memory _ensName) 
        external 
        view 
        returns (bool) 
    {
        return names[_ensName] != address(0);
    }

    /**
     * @notice Get all registered names
     */
    function getAllNames() external view returns (string[] memory) {
        return registeredNames;
    }

    /**
     * @notice Get registered name count
     */
    function getNameCount() external view returns (uint256) {
        return registeredNames.length;
    }

    /**
     * @notice Transfer ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid address");
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}","keccak256":"0x48611d050051d93d364ac444ac2a9e8319cfbac05bab7c32584b68899a17cf8b","license":"MIT"},"contracts/Station.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

/**
 * @title Station
 * @notice Individual station contract storing report CIDs and AI safety scores
 */
contract Station {

    // ==================== STATE ====================

    string public stationId;
    string public name;
    string public metro;
    string public ensName;
    address public factory;

    string[] public reportCIDs;
    uint256 public aiSafetyScore; // 0-100
    uint256 public lastUpdated;

    // ==================== EVENTS ====================

    event ReportAdded(string cid, uint256 timestamp);
    event SafetyScoreUpdated(uint256 newScore, uint256 timestamp);

    // ==================== MODIFIERS ====================

    modifier onlyFactory() {
        require(msg.sender == factory, "Only factory can call");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor(
        string memory _stationId,
        string memory _name,
        string memory _metro,
        string memory _ensName
    ) {
        stationId = _stationId;
        name = _name;
        metro = _metro;
        ensName = _ensName;
        factory = msg.sender;
        aiSafetyScore = 50; // Default neutral score
        lastUpdated = block.timestamp;
    }

    // ==================== FUNCTIONS ====================

    /**
     * @notice Add a report CID to this station
     */
    function addReportCID(string memory _cid) external onlyFactory {
        reportCIDs.push(_cid);
        lastUpdated = block.timestamp;
        emit ReportAdded(_cid, block.timestamp);
    }

    /**
     * @notice Update AI safety score
     */
    function updateSafetyScore(uint256 _score) external onlyFactory {
        require(_score <= 100, "Score must be 0-100");
        aiSafetyScore = _score;
        lastUpdated = block.timestamp;
        emit SafetyScoreUpdated(_score, block.timestamp);
    }

    /**
     * @notice Get all report CIDs
     */
    function getReportCIDs() external view returns (string[] memory) {
        return reportCIDs;
    }

    /**
     * @notice Get report count
     */
    function getReportCount() external view returns (uint256) {
        return reportCIDs.length;
    }

    /**
     * @notice Get recent reports (last N)
     */
    function getRecentReports(uint256 _count) external view returns (string[] memory) {
        uint256 length = reportCIDs.length;
        if (_count > length) _count = length;

        string[] memory recent = new string[](_count);
        for (uint256 i = 0; i < _count; i++) {
            recent[i] = reportCIDs[length - _count + i];
        }
        return recent;
    }
}","keccak256":"0xed6fcaa3c4247799baea738dfde40fc4c601240c5692c3061af4a89f415ae325","license":"MIT"},"contracts/StationFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./Station.sol";
import "./MockENSRegistry.sol";

/**
 * @title StationFactory
 * @notice Deploys Station contracts and registers ENS names
 */
contract StationFactory {

    // ==================== STATE ====================

    address public owner;
    MockENSRegistry public ensRegistry;

    mapping(string => address) public stations; // stationId => Station contract
    string[] public stationIds;

    // ==================== EVENTS ====================

    event StationDeployed(
        string stationId,
        string name,
        string ensName,
        address stationContract
    );

    // ==================== MODIFIERS ====================

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor(address _ensRegistry) {
        owner = msg.sender;
        ensRegistry = MockENSRegistry(_ensRegistry);
    }

    // ==================== FUNCTIONS ====================

    /**
     * @notice Deploy a new station contract and register ENS name
     */
    function deployStation(
        string memory _stationId,
        string memory _name,
        string memory _metro,
        string memory _ensName
    ) external onlyOwner returns (address) {
        require(stations[_stationId] == address(0), "Station exists");

        // Deploy Station contract
        Station station = new Station(_stationId, _name, _metro, _ensName);
        address stationAddress = address(station);

        // Store mapping
        stations[_stationId] = stationAddress;
        stationIds.push(_stationId);

        // Register in mock ENS
        ensRegistry.registerName(_ensName, stationAddress);

        emit StationDeployed(_stationId, _name, _ensName, stationAddress);

        return stationAddress;
    }

    function registerENSName(string memory _ensName, address _address) 
        external 
        onlyOwner 
    {
        ensRegistry.registerName(_ensName, _address);
    }

    /**
     * @notice Add report CID to a station
     */
    function addReportToStation(
        string memory _stationId,
        string memory _cid
    ) external {
        address stationAddress = stations[_stationId];
        require(stationAddress != address(0), "Station not found");

        Station(stationAddress).addReportCID(_cid);
    }

    /**
     * @notice Update safety score for a station
     */
    function updateStationSafetyScore(
        string memory _stationId,
        uint256 _score
    ) external onlyOwner {
        address stationAddress = stations[_stationId];
        require(stationAddress != address(0), "Station not found");

        Station(stationAddress).updateSafetyScore(_score);
    }

    /**
     * @notice Get station contract address by ID
     */
    function getStationAddress(string memory _stationId) 
        external 
        view 
        returns (address) 
    {
        return stations[_stationId];
    }

    /**
     * @notice Get all station IDs
     */
    function getAllStationIds() external view returns (string[] memory) {
        return stationIds;
    }

    /**
     * @notice Get station count
     */
    function getStationCount() external view returns (uint256) {
        return stationIds.length;
    }
}","keccak256":"0x025232e6ee08e912929399f25f82272e3fcedd026153fb65ad583cb98af58307","license":"MIT"}},"version":1}
